mapper.update:
UPDATE board

	board_title = #{boardTitle},
	board_type = #{boardType},
	board_category = #{boardCategory},
	board_email = #{boardEmail} 

WHERE board_no = #{boardNo : NUMERIC}

UPDATE post
post_title = #{postTitle}, post_Contents = #{postContents}, post_thombnail = #{postThombnail}, post_notice = #{postNotice}, post_origin_no = #{postOriginNo}, post_ord = #{postOrd}, post_layer = #{postLayer}
WHERE post_no = #{postNo}

UPDATE BOARD_AUTHORITY
AUTHORITY_LIST = #{authorityList}, AUTHORITY_VIEW = #{authorityView}, AUTHORITY_WRITE = #{authorityWrite}, AUTHORITY_UPDATE = #{authorityUpdate}, AUTHORITY_DELETE = #{authorityDelete}, AUTHORITY_ANSWER = #{authorityAnswer}, AUTHORITY_NOTICE = #{authorityNotice}, AUTHORITY_COMMENT = #{authorityComment}
WHERE board_no = #{authorityBoard.boardNo : NUMERIC}

UPDATE BOARD_OPTION
OPTION_NOTICE = #{optionNotice}, OPTION_COMMENT = #{optionComment}, OPTION_SECRET = #{optionSecret}, OPTION_ANSWER = #{optionAnswer}, OPTION_CAPTCHA = #{optionCaptcha}, OPTION_MASS = #{optionMass}, OPTION_ORDER = #{optionOrder}, OPTION_ADDINFO = #{optionAddinfo}, OPTION_POPUP = #{optionPopup}
WHERE board_no = #{optionBoard.boardNo : NUMERIC}
