mapper.update:
UPDATE
	member_grade
SET 
	member_grade_name = #{memberGradeName}
WHERE
	member_grade_no = #{memberGradeNo}

UPDATE 
	authorities
SET
	authority = #{authority}
WHERE
	member_no = #{memberNo}

UPDATE 
	member_memo
SET
	member_memo_content = #{memberMemoContent}
WHERE
	member_no = #{memberNo}

mapper.select:
select d.* from (select @ROWNUM:=@ROWNUM+1 as rowNum, c.* from (select @ROWNUM:=0, a.MEMBER_POINT_NO, b.id, CONCAT(b.LAST_NAME, b.FIRST_NAME) AS member_name, b.EMAIL, a.point_title, a.point_amount, a.reg_date FROM member_point a JOIN member b ON(a.MEMBER_NO = b.MEMBER_NO)
${field} LIKE CONCAT('%',#{keyword},'%')
and a.member_no = #{memberNo}
order by a.reg_date DESC) as c) as d where d.rowNum BETWEEN #{startRow} AND #{endRow}

select COUNT(*) from member_point a join member b ON(a.MEMBER_NO = b.MEMBER_NO)
${field} LIKE CONCAT('%',#{keyword},'%')
and a.member_no = #{memberNo}

mapper.update:
UPDATE 
	withdrawal_member
SET
	reason = #{reason}
WHERE
	member_no = #{memberNo}

mapper.select:

select *
from member
where member_no = #{code}

select phone
  from member
 where id = #{id}

select id
  from member
 where member_no = #{memberNo}
