mapper.select:
SELECT *
FROM menu
WHERE code = #{code}
AND	revision_code = #{revision_code}

SELECT *
FROM head
ORDER BY HEAD_NO ASC;

mapper.update:
UPDATE menu
CATEGORY_CODE = #{category_code}, REVISION_CODE = #{revision_code}, TITLE = #{title : VARCHAR}, CONTENT = #{content : VARCHAR}, IS_OUTER_LINK =#{is_outer_link : VARCHAR}, TARGET = #{target : VARCHAR}, URL =#{url : VARCHAR}, STATUS = #{status : VARCHAR}, MENU_HEAD_CODE = #{menu_head_code : NUMERIC}, IS_META = #{is_meta : VARCHAR}, META_TITLE = #{meta_title : VARCHAR}, META_DESCRIPTION = #{meta_description : VARCHAR}
WHERE CODE = #{code:NUMERIC}

UPDATE head

	TITLE = #{title : VARCHAR},
	STATUS = #{status : VARCHAR},
	CONTENT = #{content : VARCHAR}

WHERE HEAD_NO = #{headNo : NUMERIC}

UPDATE bottom

	CONTENT = #{content : VARCHAR}

WHERE BOTTOM_NO = 1

UPDATE meta
META_TITLE = #{meta_title : VARCHAR}, META_DESCRIPTION = #{meta_description : VARCHAR}
WHERE META_NO = 1
