mapper.select:
SELECT DISTINCT *, (SELECT count(amount) FROM v_product_statistics WHERE order_status_no >= 2) paymentCount, (SELECT COUNT(amount) FROM v_product_statistics WHERE order_status_no = 7) returnCount, (SELECT SUM(amount) FROM v_product_statistics WHERE order_status_no >= 2) paymentSum, (ROW_NUMBER() over(ORDER BY (SELECT COUNT(amount) FROM v_product_statistics WHERE order_status_no >= 2 AND order_status_no != 7) DESC)) AS productRank FROM v_product_statistics
WHERE reg_date between #{search.start_date} and #{search.end_date}
AND category_no = #{search.categoryCode}
ORDER BY productRank ASC

SELECT NAME, id, SUM(amount) AS total_amount, SUM(cancel_amount) AS total_amount_cancel, (sum(amount) - sum(ifnull(cancel_amount,0))) AS total, RANK() over(ORDER BY total DESC) AS ranking, COUNT(amount) AS order_cnt, COUNT(cancel_amount) AS cancel_cnt FROM v_member_statistics_paid LEFT JOIN v_member_statistics_cancel USING(member_no)
WHERE paid_at <= #{endDate} and paid_at >= #{startDate}
OR cancel_at <= #{endDate} and cancel_at >= #{startDate}
GROUP BY member_no
ORDER BY ranking LIMIT 10
